/* eslint-disable @typescript-eslint/no-var-requires */
const CliTable3 = require('./src/table');
function getCustomColourTable(colour) {
	return new CliTable3({
		chars: {
			top: colour('─'),
			'top-mid': colour('┬'),
			'top-left': colour('┌'),
			'top-right': colour('┐'),
			bottom: colour('─'),
			'bottom-mid': colour('┴'),
			'bottom-left': colour('└'),
			'bottom-right': colour('┘'),
			left: colour('│'),
			'left-mid': colour('├'),
			mid: colour('─'),
			'mid-mid': colour('┼'),
			right: colour('│'),
			'right-mid': colour('┤'),
			middle: colour('│')
		}
	});
}

module.exports = CliTable3;
module.exports.getCustomColourTable = getCustomColourTable;
